import React from 'react';
import axios from 'axios';
import { isNotUndefined } from './Validator';

// notes
// ada masalah dengan react hooks sehingga showSnackbar dijadikan parameter dan disupply dari component

export default function CommonNetworkCall(path, method, data = '', headers = {}, showSnackbar) {
  const BASE_URL = process.env.NEXT_PUBLIC_API_BASE_URL;
  const url = `${BASE_URL}${path}`;
  console.log('hm');

  let _axios;
  switch (method) {
    case 'get':
      _axios = axios.get(url);
      break;
    case 'post':
      _axios = axios.post(url, data, { headers });
      break;
    case 'delete':
      _axios = axios.delete(url, { headers });
      break;
    case 'put':
      _axios = axios.put(url, data, { headers });
      break;
    default:
      _axios = axios();
  }
  _axios
    .then(async (apiResponse) => {
      const { response, result } = apiResponse.data;

      if (response.code !== '000000') {
        showSnackbar('Terjadi Gangguan', 'error');
        console.log('ah');
        return null;
      }
      console.log('ugh');
      return result;
    })
    .catch(async (error) => {
      if (isNotUndefined(error.response)) {
        const { data } = error.response;
        showSnackbar(data.response.user_errors, 'error');
      } else {
        showSnackbar(error.toString(), 'error');
      }
      console.log('ew');
      return null;
    });
}
