import React from 'react';
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { isNotUndefined } from './Validator';
import { showSnackbar } from '../redux/snackbar/action';

export const CommonNetworkCall = async (path, method, data = '', headers = {}) => {
  const dispatch = useDispatch();

  const BASE_URL = process.env.NEXT_PUBLIC_API_BASE_URL;
  const url = `${BASE_URL}${path}`;

  let _axios;
  switch (method) {
    case 'get':
      _axios = axios.get(url);
      break;
    case 'post':
      _axios = axios.post(url, data, { headers });
      break;
    case 'delete':
      _axios = axios.delete(url, { headers });
      break;
    case 'put':
      _axios = axios.put(url, data, { headers });
      break;
    default:
      _axios = axios();
  }
  await _axios
    .then(async (apiResponse) => {
      const { response, result } = apiResponse.data;

      if (response.code !== '000000') {
        dispatch(showSnackbar('Terjadi Gangguan', 'error'));
        return null;
      }
      return result;
    })
    .catch(async (error) => {
      if (isNotUndefined(error.response)) {
        const { data } = error.response;
        dispatch(showSnackbar(data.response.user_errors, 'error'));
      } else {
        dispatch(showSnackbar(error.toString(), 'error'));
      }
      return null;
    });
};
