import React, { Component } from 'react';
import Grid from '@material-ui/core/Grid';

// Component
import Dialog from '@material-ui/core/Dialog';
import DialogContent from '@material-ui/core/DialogContent';

// Redux
import ButtonMain from './ButtonMain';

export class CustomDialog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
    };
  }

  onClick(method) {
    this.setState({ isOpen: false });
    method();
  }

  render() {
    const {
      title, content, onConfirm, onCancel, open, cancelText, confirmText,
    } = this.props;
    this.setState({ isOpen : open });
    return (
      <Dialog
        open={this.state.isOpen}
        fullWidth
        maxWidth="xs"
        onClose={() => this.setState({ isOpen: false })}
      >
        <DialogContent className="txt-center-hoz">
          <p className="font-muli-14 bold">{title}</p>
          <p className="font-muli-14 margin-btm-24">{content}</p>
          <Grid
            container
            direction="row"
            justify="space-between"
            spacing={2}
          >
            <Grid item xs={6}>
              <ButtonMain id="btnClose" onClick={() => this.onClick(onCancel)} color="secondary" width="fullWidth">{cancelText}</ButtonMain>
            </Grid>
            <Grid item xs={6}>
              <ButtonMain id="btnNext" onClick={() => this.onClick(onConfirm)} color="primary" width="fullWidth">{confirmText}</ButtonMain>
            </Grid>
          </Grid>

        </DialogContent>

      </Dialog>

    );
  }
}
export default CustomDialog
