import React, { Component } from 'react';
import Router from 'next/router';
import moment from 'moment';

// material
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
// component
import { connect } from 'react-redux';
import InfiniteScroll from 'react-infinite-scroll-component';
import ButtonBack from '../../components/global-component/ButtonBack';

// redux

import { showSnackbar } from '../../redux/snackbar/action';
import { getRupiah } from '../../utils/Calculate';

export class Riwayat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      historyList: [],
      hasMore: true,
    };
  }

  componentDidMount() {
    // document.body.className = 'soft-blue-body';
    this.getHistoryList();
  }

  componentWillUnmount() {
    // document.body.className = null;
  }

  getHistoryList() {
    this.setState({
      hasMore: false,
      historyList: [{
        order_id: '123',
        total: '10250000',
        created: '2020-11-14 00:00:00',
        items: [
          {
            product_name: 'pulsa 10',
          },
          {
            product_name: 'pulsa 20',
          },
        ],
      }],
    });
  }

  render() {
    const { hasMore, historyList } = this.state;
    return (
      <div className="soft-blue-body">
        <div className="main-content" id="mainContent">
          <ButtonBack id="btnBack" onClick={() => Router.back()} />

          <InfiniteScroll
            dataLength={historyList.length}
            next={() => this.getHistoryList()}
            hasMore={hasMore}
            loader={<h4>Memuat...</h4>}
          >
            {historyList.map((product) => (
              <Paper className="padding-16 margin-btm-16">
                <div className="flex margin-btm-16">
                  <p className="font-muli-14 bold">{moment(product.create).locale('ID').fromNow()}</p>
                  <p
                    id="btnDetail"
                    className="light-blue margin-lft-auto bold font-muli-14 pointer"
                    onClick={() => Router.push('/riwayat/1234')}
                  >
                    DETAIL PEMBELIAN
                  </p>
                </div>
                <Divider />
                <div className="flex margin-top-16">
                  <p className="font-muli-14 bold">Item:</p>
                  {/*{product.items.map((item) => (*/}
                  {/*  <p className="margin-lft-auto font-muli-16">{item.product_name}</p>*/}
                  {/*))}*/}
                  <p className="margin-lft-auto font-muli-16">{item.product_name}</p>
                </div>
                <Grid container direction="row" justify="space-between" className="txt-center-ver margin-top-16">
                  <Grid item xs={2}>
                    <p className="font-muli-14 bold">Item:</p>
                  </Grid>
                  <Grid item xs={10}>
                    {product.items.map((item) => (
                      <p className="font-muli-16">{item.product_name}</p>
                    ))}
                  </Grid>
                </Grid>
                <Divider />
                <div className="flex margin-top-16">
                  <p className="font-muli-14 bold">Total pembayaran:</p>
                  <p className="margin-lft-auto font-muli-16">
                    {getRupiah(product.total)}
                  </p>
                </div>
              </Paper>
            ))}
          </InfiniteScroll>

          <br />
        </div>
      </div>

    );
  }
}

const mapDispatchToProps = {
  showSnackbar,
};
export default connect(null, mapDispatchToProps)(Riwayat);
