/**
 * @method showSnackbar
 * @summary to set snackbar that will displayed to user
 * @param message
 * @param snackType
 * @returns {function(...[*]=)}
 */
export const showSnackbar = (message, snackType) => {
  return dispatch => {
    dispatch({
      type: "SNACKBAR_SUCCESS",
      message,
      snackType
    });
  };
};
/**
 * @summary to clear snackbar that will change state on reducer
 * @returns {function(...[*]=)}
 */

export const clearSnackbar = () => {
  return dispatch => {
    dispatch({
      type: "SNACKBAR_CLEAR"
    });
  };
};
