import React, { Component } from 'react';
import Router from 'next/router';

// material
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
// component
import { connect } from 'react-redux';
import InfiniteScroll from 'react-infinite-scroll-component';
import ButtonBack from '../../components/global-component/ButtonBack';

// redux

import { showSnackbar } from '../../redux/snackbar/action';
import { getRupiah } from '../../utils/Calculate';

export class Riwayat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      historyList: [],
      hasMore: true,
    };
  }

  componentDidMount() {
    document.body.style.background = 'rgb(239, 243, 255)';
    this.getHistoryList();
  }

  getHistoryList() {
    this.setState({
      hasMore: false,
      historyList: [{
        product_type: 'Pulsa',
        product_name: 'TKSL 100000',
        order_identifier: '082335530143',
        total: '10250000',
      }],
    });
  }

  render() {
    const { hasMore, historyList } = this.state;
    return (

      <div className="main-content" id="mainContent">
        <ButtonBack id="btnBack" onClick={() => Router.back()} />

        <InfiniteScroll
          dataLength={historyList.length}
          next={() => this.getHistoryList()}
          hasMore={hasMore}
          loader={<h4>Memuat...</h4>}
        >
          {historyList.map((product) => (
            <Paper className="padding-16 margin-btm-16">
              <div className="flex margin-btm-16">
                <p className="font-muli-14 bold">{product.product_type}</p>
                <p
                  id="btnDetail"
                  className="light-blue margin-lft-auto bold font-muli-14 pointer"
                  onClick={() => Router.push('/riwayat/1234')}
                >
                  DETAIL PEMBELIAN
                </p>
              </div>
              <Divider />
              <Grid container direction="row" justify="space-between" className="txt-center-ver margin-top-16">
                <Grid item xs={7}>
                  <p className="font-muli-14 bold">{product.product_name}</p>
                  <p id="productIdentifier" className="font-muli-12 max-15-char">{product.order_identifier}</p>
                </Grid>
                <Grid item xs={5}>
                  <p className="font-muli-16 txt-end">{getRupiah(product.total)}</p>
                </Grid>
              </Grid>
            </Paper>
          ))}
        </InfiniteScroll>

        <br />
      </div>

    );
  }
}

const mapDispatchToProps = {
  showSnackbar,
};
export default connect(null, mapDispatchToProps)(Riwayat);
