import React, { Component } from 'react';
import moment from 'moment';
import axios from 'axios';
import Router from 'next/router';
// material
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Dialog from '@material-ui/core/Dialog';
import CircularProgress from '@material-ui/core/CircularProgress';

// component
import { connect } from 'react-redux';
import Divider from '@material-ui/core/Divider';
import ButtonNavigationPay from '../../../components/global-component/ButtonNavigationPay';
import ProductCard from '../../../components/global-component/ProductCard';
import MainLogo from '../../../components/global-component/MainLogo';
import CompanyLogo from '../../../components/global-component/CompanyLogo';
import { getRupiah } from '../../../utils/Calculate';
import { getHashUrl } from '../../../utils/CommonUtils';
import PaymentMethod from '../../../components/PaymentMethod';
import BasicConfirmationDialog from '../../../components/global-component/BasicConfirmationDialog';

// redux
import { showSnackbar } from '../../../redux/snackbar/action';
import { isNotUndefined } from '../../../utils/Validator';
import ProductOrderDetail from '../../../components/status-pembayaran/ProductOrderDetail';
import { showDialogOrderDetail } from '../../../redux/productOrderDetail/action';
import { showConfirmationDialog } from '../../../redux/basicConfimationDialog/action';
import { additionalProductURL } from '../../../utils/MappingIdToUrl';
import { isUserLoggedValid } from '../../../utils/NetworkCall';
import CustomDialog from '../../../components/global-component/CustomDialog';
import { featureEnable } from '../../../utils/Flagr';

export class DetailTagihan extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isError: true,
      isLoading: true,
      customer_id: 0,
      orderId: 0,
      openDialogPayment: false,
      companyLogo: '',
      data: {
        customer_name: '-',
        items: [
          {
            id: 0,
            product_type_name: '-',
            product_name: '-',
            biller_name: '-',
            data: {
              billing_id: 0,
              product_name: '-',
              periode: '-',
              start_date: '-',
              end_date: '-',
              details: [
                {
                  id: 0,
                  billing_id: 0,
                  billing_type_name: '-',
                  amount: 0,
                },
              ],
            },
            sell_price: 0,
            total: 0,
            order_identifier: '-',
          },
        ],
        total: 0,
      },
      additionalProduct: [],
    };
  }

  async componentDidMount() {
    document.body.className = 'body-blue';
    const orderId = getHashUrl();
    this.setState({ orderId });
    // if (await featureEnable(6))
    this.setState({ login: await isUserLoggedValid() });
    if (this.state.login === 'logged') this.inquiry(orderId);
  }

  componentWillUnmount() {
    document.body.className = null;
  }

  /**
   * @method Inquiry
   * @summary do inquiry of bayartagihan product
   * @param hash
   * @returns {Promise<void>}
   */
  async inquiry(hash) {
    const { showSnackbar } = this.props;

    const BASE_URL = process.env.NEXT_PUBLIC_API_BASE_URL;
    const url = `${BASE_URL}/inquiry/atur_tagihan`;
    let headers = {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${JSON.parse(localStorage.getItem('token')).access_token}`
    };

    //if (this.state.login === 'logged') {
      // headers = { ...headers, Authorization: `Bearer ${JSON.parse(localStorage.getItem('token')).access_token}` };
    //}

    const data = JSON.stringify({
      order_identifier: hash,
    });

    await axios
      .post(url, data, { headers })
      .then(async (apiResponse) => {
        const { response, result } = apiResponse.data;
        if (response.code !== '000000') {
          showSnackbar('Terjadi Gangguan', 'error');
          this.setState({ isLoading: false });
        } else if (result.is_processed === 1) {
          Router.push(`/status-pembayaran/${hash}`);
          this.setState({ isLoading: false });
        } else {
          this.setState({ customer_id: result.customer_id, companyLogo: result.data.product_logo });
          this.getOrderDetail(result.customer_id);
        }
      }).catch((error) => {
        if (isNotUndefined(error.response)) {
          const { data } = error.response;
          if (data.response.code === '000007') {
            Router.push('/url-not-found');
          }
          // payment by b2b
          else if (data.response.code === '000008') {
            Router.push('/pembayaran-berhasil');
          } else if (data.response.code === '000009') {
            Router.push('/expired-bill');
          }
          showSnackbar(data.response.user_errors, 'error');
        } else {
          showSnackbar(error.toString(), 'error');
        }
        this.setState({ isLoading: false });
      });
  }

  /**
   * @method getOrderDetail
   * @summary get detail of given order id by url and set it back to state (CART)
   * @param order_id
   * @returns {Promise<void>}
   */
  async getOrderDetail(custId) {
    const { showSnackbar } = this.props;
    const BASE_URL = process.env.NEXT_PUBLIC_API_BASE_URL;
    const url = `${BASE_URL}/cart/atur_tagihan/${custId}`;

    await axios
      .get(url)
      .then(async (apiResponse) => {
        const { response, result } = apiResponse.data;

        if (response.code !== '000000') {
          showSnackbar('Terjadi Gangguan', 'error');
        } else {
          this.setState({ data: result, isError: false });
          localStorage.setItem('cId', custId);
          this.getAdditionalProduct();
        }
        this.setState({ isLoading: false });
      })
      .catch(async (error) => {
        if (isNotUndefined(error.response)) {
          const { data } = error.response;
          showSnackbar(data.response.user_errors, 'error');
        } else {
          showSnackbar(error.toString(), 'error');
        }
        this.setState({ isLoading: false });
      });
  }

  /**
   * @summary mendapatkan produk tambahan
   * @returns {Promise<void>}
   */
  async getAdditionalProduct() {
    const { showSnackbar } = this.props;
    const BASE_URL = process.env.NEXT_PUBLIC_API_BASE_URL;
    const url = `${BASE_URL}/product_type/available/atur_tagihan?is_special__equals=0`;

    await axios
      .get(url)
      .then(async (apiResponse) => {
        const { response, result } = apiResponse.data;

        if (response.code !== '000000') {
          showSnackbar('Terjadi gangguan dalam mengambil produk tambahan', 'error');
        } else {
          this.setState({ additionalProduct: result });
        }
      })
      .catch(async () => {
        showSnackbar('Terjadi gangguan dalam mengambil produk tambahan', 'error');
      });
  }

  /**
   * @summary menghapus produk tambahan
   * @param cartId
   * @returns {Promise<void>}
   */
  async deleteCartItem(cartId) {
    this.setState({ isLoading: true });
    const { showSnackbar } = this.props;
    const { customer_id } = this.state;
    const BASE_URL = process.env.NEXT_PUBLIC_API_BASE_URL;
    const url = `${BASE_URL}/cart/atur_tagihan/${customer_id}/${cartId}`;
    await axios
      .delete(url)
      .then(async (apiResponse) => {
        const { response } = apiResponse.data;
        if (response.code !== '000000') {
          showSnackbar('Terjadi gangguan ketika menghapus produk tambahan', 'error');
          this.setState({ isLoading: false });
        } else {
          this.getOrderDetail(customer_id);
        }
      })
      .catch(async () => {
        showSnackbar('Terjadi gangguan ketika menghapus produk tambahan', 'error');
        this.setState({ isLoading: false });
      });
  }

  render() {
    const { showDialogOrderDetail, showConfirmationDialog } = this.props;
    const {
      data, isError, customer_id, openDialogPayment, isLoading,
      orderId, companyLogo, additionalProduct, login,
    } = this.state;
    const bayarTagihan = data.items[0];
    const additionalProductCart = data.items.slice(1);

    let dialogLogin;
    if (login === 'empty' || login === 'expired' || login === 'inactive') {
      let title;
      switch (login) {
        case 'expired':
          title = 'Sesi Anda Telah Habis';
          break;
        case 'inactive':
          title = 'Akun Anda Tidak Aktif atau Terblokir';
          break;
        default:
          title = 'Pemberitahuan';
      }
      dialogLogin = (
        <CustomDialog
          title={title}
          content="Silahkan login menggunakan akun google anda untuk melanjutkan transaksi ini."
          // cancelText="Lanjutkan tanpa login"
          confirmText="Login"
          // onCancel={() => localStorage.removeItem('token')}
          onConfirm={() => Router.push({ pathname: '/login', query: { ref: `/detail-tagihan/${this.state.orderId}` } })}
          open
          mode="vertical"
          block
        />
      );
    }

    return (
      <div>
        <MainLogo />
        {isLoading ? (
          <CircularProgress style={{ position: 'absolute', left: '47%', top: '50%' }} />
        ) : (
          <div className="main-content" id="mainContent">
            <Paper id="mainSection" className="padding-22" elevation={0}>
              <CompanyLogo className="margin-btm-32" src={companyLogo} />

              <Grid
                container
                direction="row"
                justify="space-between"
                className="margin-btm-32 txt-center-ver"
              >
                <Grid>
                  <p className="font-muli-14 bold">Kode Tagihan</p>
                </Grid>
                <Grid>
                  <p
                    id="orderId"
                    className="font-muli-14 bold txt-end rounded-bg max-15-char"
                  >
                    {bayarTagihan.data.billing_id}
                  </p>
                </Grid>
              </Grid>

              <Grid
                container
                direction="row"
                justify="space-between"
                className="margin-btm-32"
              >
                <Grid item xs={6}>
                  <p className="font-muli-300">Nama Pelanggan</p>
                  <p className="font-muli-16 bold">{data.customer_name}</p>
                </Grid>
                <Grid item xs={6}>
                  <p className="font-muli-300 txt-end">Jatuh Tempo</p>
                  <p className="font-muli-16 bold txt-end">
                    {moment(bayarTagihan.data.end_date, 'YYYY-MM-DD hh:mm:ss')
                      .locale('ID')
                      .format('LL')}
                  </p>
                </Grid>
              </Grid>

              <p className="font-muli-14 bold">Rincian tagihan</p>

              {bayarTagihan.data.details.map((detail) => (
                <Grid
                  id={detail.id}
                  container
                  direction="row"
                  justify="space-between"
                >
                  <Grid>
                    <p className="font-muli-14">{detail.billing_type_name}</p>
                  </Grid>
                  <Grid>
                    <p className="font-muli-16">{getRupiah(detail.amount)}</p>
                  </Grid>
                </Grid>
              ))}
            </Paper>

            {additionalProductCart.length > 0
              ? (
                <div id="additionalProductCart">
                  <p className="font-muli-14 bold margin-btm-16 margin-top-24">Produk Tambahan</p>
                  {additionalProductCart.map((item) => (
                    <Paper className="padding-22 margin-btm-16" elevation={0}>
                      <Grid container direction="row" justify="space-between" className="txt-center-ver margin-btm-16">
                        <Grid>
                          <p className="font-muli-14 bold">{item.product_type_name}</p>
                        </Grid>
                        <Grid>
                          <p
                            id="deleteAdditional"
                            onClick={() => showConfirmationDialog('Hapus produk tambahan', 'Apakah anda yakin?', () => this.deleteCartItem(item.id))}
                            className="font-muli-14 bold light-blue"
                          >
                            HAPUS
                          </p>
                        </Grid>
                      </Grid>
                      <Divider />
                      <Grid container direction="row" justify="space-between" className="txt-center-ver margin-top-16">
                        <Grid item xs={7}>
                          <p className="font-muli-14 bold">{item.product_name}</p>
                          <p className="font-muli-12 max-15-char">{item.order_identifier}</p>
                        </Grid>
                        <Grid item xs={5}>
                          <p className="font-muli-16 txt-end">{getRupiah(item.total)}</p>

                        </Grid>
                      </Grid>
                    </Paper>
                  ))}
                  <BasicConfirmationDialog />
                </div>
              )
              : null}

            <p className="font-muli-14 bold margin-tp-btm-22">
              Tambahkan ke tagihan ini.
            </p>

            {additionalProduct.length > 0
              ? (
                <div id="secondSection" className="horizontal-scroll">
                  {additionalProduct.map((item) => (
                    <ProductCard
                      id={`product${item.id}`}
                      title={item.name}
                      logo={item.icon}
                      onClick={() => Router.push(`${additionalProductURL(item.id)}/${orderId}`)}
                    />
                  ))}
                </div>
              )
              : null}

          </div>
        )}

        <ButtonNavigationPay
          totalPayment={getRupiah(data.total)}
          onClick={() => {
            this.setState({ openDialogPayment: true });
          }}
          onDetailClick={() => showDialogOrderDetail(data.items, data.total, data.admin_fee)}
          disabled={isError}
        />

        <Dialog fullScreen open={openDialogPayment}>
          <PaymentMethod
            cartDetail={data}
            custId={customer_id}
            orderId={orderId}
            onClose={() => this.setState({ openDialogPayment: false })}
          />
        </Dialog>
        <ProductOrderDetail />
        {dialogLogin}
      </div>
    );
  }
}

const mapDispatchToProps = {
  showSnackbar, showDialogOrderDetail, showConfirmationDialog,
};

export default connect(null, mapDispatchToProps)(DetailTagihan);
